INSERT INTO `bhm_customer_care` (`id`, `title_ru`, `title_en`, `html_ru`, `html_en`, `sort`, `status`) VALUES
(1, 'Служба поддержки', 'Customer Care', '<p>Добро пожаловать на сайт Berhasm.com\r\n</p><p>Наша\r\nслужба поддержки работает с понедельника\r\nпо пятницу и будет рада ответить на ваши\r\nвопросы или сопроводить вас в вашем\r\nзнакомстве с брендом.\r\n</p><p>Связаться с нашей\r\nкомандой можно по электронному адресу\r\ncontact@berhasm.com\r\n</p>', '<p>Welcome to berhasm.com\r\n</p><p>Our Customer Service is available from Monday to Friday and will be happy to answer your inquiries or accompany you in discovering the brand and its products.\r\n</p><p>Contact our team at contact@berhasm.com\r\n</p>', 1, 1),
(2, 'Доставка', 'Delivery', '<p>berhasm.com\r\nосуществляет доставку по всему миру.\r\n</p><h3><br></h3><h3>Доставка по Европе</h3><p><span></span>DHL, 2-5 рабочих дней. бесплатно при\r\nзаказе свыше 20 тыс. рублей\r\n</p><p>При заказе меньше 20\r\nтыс. рублей доставка 2000 рублей\r\n</p><h3>Доставка по США</h3><p><span></span>DHL, 2-5 рабочих дней. бесплатно при\r\nзаказе свыше 20 тыс. рублей\r\n</p><p>При заказе меньше 20\r\nтыс. рублей доставка 2500 рублей\r\n</p><h3>Доставка по Азии</h3><p><span></span>DHL, 2-5 рабочих дней. бесплатно при\r\nзаказе свыше 20 тыс. рублей\r\n</p><p>При заказе меньше 20\r\nтыс. рублей доставка 3000 рублей\r\n</p><h3>Доставка по Австралии</h3><p><span></span>DHL, 2-5 рабочих дней. бесплатно при\r\nзаказе свыше 20 тыс. рублей\r\n</p><p>При заказе меньше 20\r\nтыс. рублей доставка 3000 рублей\r\n</p><h3>Доставка по Ближнему\r\nВостоку\r\n</h3><p><span></span>DHL, 2-5 рабочих дней. бесплатно при\r\nзаказе свыше 20 тыс. рублей\r\n</p><p>При заказе меньше 20\r\nтыс. рублей доставка 3000 рублей\r\n</p><p><br>\r\n</p><p><strong>Как только ваш заказ\r\nбудет отгружен с нашего склада, на ваш\r\ne-mail будет\r\nотправлено письмо с номером трекинга.\r\nНаши курьеры работают с понедельника\r\nпо пятницу в рабочее время.\r\n	</strong>\r\n</p>', '<p>berhasm.com ships worldwide.\r\n</p><h3><br></h3><h3>Europe\r\n</h3><p>DHL  2-5 business days  Free on orders over 300 EUR\r\n</p><p>25 EUR on all other orders\r\n</p><h3>US\r\n</h3><p>DHL 2-5 business days  Free on orders over 300 EUR\r\n</p><p>30 EUR on all other orders\r\n</p><h3>Asia\r\n</h3><p>DHL  2-5 business days  Free on orders over 300 EUR\r\n</p><p>35 EUR on all other orders\r\n</p><h3>Australia\r\n</h3><p>DHL  2-5 business days  Free on orders over 300 EUR\r\n</p><p>35 EUR on all other orders\r\n</p><h3>Middle East\r\n</h3><p>DHL  2-5 business days  Free on orders over 300 EUR\r\n</p><p>35 EUR on all other orders\r\n</p><p><br></p><p><strong>Once your package has been shipped from our warehouse, you will receive an email including a tracking number. Our couriers delivers from Monday to Friday during business hours.</strong>\r\n</p>', 2, 1),
(3, 'Возвраты', 'Returns', '<p>Вы можете вернуть любую вещь, купленную\r\nв berhasm.com в\r\nтечение 14 дней с момента получения.\r\n</p><p>* Вещи можно вернуть\r\nв первоначальном виде в полной\r\nкомплектации, если вы сохранили упаковку\r\nи ярлыки\r\n</p><p>* Будучи международным\r\nпокупателем, вы можете осуществить\r\nвозврат с помощью посылки через любую\r\nпочтовую службу. Однако, до того, как мы\r\nполучим вашу посылку с возвратом, посылка\r\nостается вашей ответственностью.\r\nПожалуйста, сохраняйте подтверждение\r\nотправки посылки\r\n</p><p>berhasm.com\r\nоставляет за собой право отказать\r\nвозвращенной посылке или при уведомлении\r\nпосле окончания периода возврата или\r\nвещи были отправлены не в их оригинальном,\r\nнеизмененном или поношенном состоянии\r\n</p><p>Вы получите возврат\r\nденег тем же методом, какой был использован\r\nдля покупки. Оплата возврата совершается\r\nклиентом. Комиссии, налоги и прочие\r\nплатежи, связанные с доставкой, возврату\r\nне подлежат.\r\n</p><p><br></p>', '<p>You can return any item purchased on berhasm.com within 14 days from the delivery date.</p><p>* Make sure the product you return is in its original condition, including original tags and packaging.</p><p>* As an international customer, you can return your parcel using any postal service. However, as the parcel remains your responsibility until it arrives to us, please remember to keep a proof of dispatch.</p><p>berhasm.com reserves the right to reject returns sent or communicated beyond the return period limit, or items which are not in its original, unaltered and unwashed condition.</p><p>You will receive the refund in the same payment method used for the purchase. Return cost is paid by client. Duties, taxes and the original shipping charges are non-refundable.</p><p><br></p>', 3, 1),
(4, 'Оплата', 'Payment', '<p>berhasm.com\r\nпредоставляет следующие возможные\r\nспособы оплаты:\r\n</p><ul>\r\n	<li>оплата кредитной\r\nили дебетовой картой Visa,\r\nVisa Electron,\r\nMastercard</li>\r\n</ul><p>berhasm.com\r\nпредлагает чекаут одним шагом.\r\n</p><p>Ваша кредитная/дебетовая\r\nкарта может быть отказана по следующим\r\nпричинам:\r\n</p><ul>\r\n	<li>У вашей карты истек\r\nпериод действия. Убедитесь, что ваша\r\nкарта работает\r\n	</li>\r\n	<li>Вы превысили ваш\r\nлимит. Свяжитесь с вашим банком, чтобы\r\nузнать, не превышен ли допустимый\r\nавторизованный лимит\r\n	</li>\r\n	<li>Возможно, часть\r\nинформации была введена некорректно.\r\n	</li>\r\n</ul><p>Каждая покупка на\r\nсайте berhasm.com\r\nосуществляется защищенными методами\r\nблагодаря внедрению современных сервисов\r\nшифрования (SSL)\r\n</p><p>Для покупок с кредитных\r\nи дебетовых карт необходимо ввести код\r\nCVV на обратной стороне\r\nкарты.\r\n</p>', '<p>Berhasm.com  accepts the following payment methods:\r\n</p><ul>\r\n	<li>Credit/Debit cards: Visa, Visa Electron, Mastercard</li>\r\n</ul><p>berhasm.com features a fast one step checkout option.</p><p>Your credit/debit card may be refused for any of the following reasons:</p><ul>\r\n\r\n	<li>Your card may have expired. Check that your card is still valid.</li>\r\n	<li>You may have reached your limit. Contact your bank to check that you have not exceeded the authorized purchase limit.</li>\r\n	<li>You may have entered some information incorrenctly.</li>\r\n</ul><p>On berhasm.com every purchase is processed with maximum security thanks to the implementation of of the most advanced enscryption services (SSL).\r\n</p><p><span></span>For payments with credit and debit cards, you are required to enter the CVV (Card Verification Value), a code printed on the card that serves as a security measure during electronic commerce transactions.\r\n</p>', 4, 1),
(5, 'Юридическая информация', 'Legal', '<p>Наименование организации: ИП Туразашвили Бесики Роинович</p><p>Тел. номер: +7 (925) 711-55-64</p><p>Адрес: 610000, Кировская обл, Кирово-Чепецк г., квартал Злобино</p><p>ИНН: 431211723265</p><p>email: contact@berhasm.com</p><p><br></p><p>ИП Туразашвили Бесики Роинович</p><p>Расчетный счет 40802810100000057574</p><p>Банк АО \"ТИНЬКОФФ БАНК\", Москва</p><p>БИК 044525974</p><p>Kорр. счет 30101810145250000974</p>', '<p>Наименование организации: ИП Туразашвили Бесики Роинович</p><p>Тел. номер: +7 (925) 711-55-64</p><p>Адрес: 610000, Кировская обл, Кирово-Чепецк г., квартал Злобино</p><p>ИНН: 431211723265</p><p>email: contact@berhasm.com</p><p><br></p><p>ИП Туразашвили Бесики Роинович</p><p>Расчетный счет 40802810100000057574</p><p>Банк АО \"ТИНЬКОФФ БАНК\", Москва</p><p>БИК 044525974</p><p>Kорр. счет 30101810145250000974</p>', 5, 0);
